const layers=document.querySelectorAll(".layer");function parallax(e){layers.forEach(t=>{let l=t.getAttribute("data-speed");t.style.transform=`translateX(${-e.clientX*l/1e3}px) translateY(${e.clientY*l/700}px)`})}function stopParallax(e){layers.forEach(e=>{e.getAttribute("data-speed");e.style.transform="translate(0)"})}document.addEventListener("mousemove",parallax),document.addEventListener("mouseout",stopParallax);const tabsProducts=document.querySelectorAll("[data-tabs-products]");for(const e of tabsProducts)e.addEventListener("click",()=>{tabsProducts.forEach(t=>{e==t?t.classList.add("products__button-active"):t.classList.remove("products__button-active")})});const productsAll=document.querySelectorAll(".card__item");for(const e of productsAll)e.addEventListener("click",()=>{let t=e.querySelector(".card__basket");console.log("productInBasket: ",t),t.classList.toggle("card__basket-active")});const menuElem=document.querySelector(".menu__nav"),buttonElem=document.querySelector(".menu__btn-small"),humburgerElem=document.querySelector(".menu__hamburger"),toggleMenu=()=>{menuElem.classList.toggle("menu__nav-active"),humburgerElem.classList.toggle("menu__hamburger-active")};buttonElem.addEventListener("click",toggleMenu),menuElem.addEventListener("click",e=>{e.target.classList.contains("menu-list__link")&&toggleMenu()});const scrollButtons=document.querySelectorAll(".scroll");for(let e of scrollButtons)e.addEventListener("click",(function(t){t.preventDefault();const l=e.getAttribute("href");document.querySelector(l).scrollIntoView({behavior:"smooth",block:"start"})}));